mapper.select:
SELECT 

FROM t_consts_classify 

	 del = 0

		AND code = #{code}


		AND parent_code = #{parentCode}


ORDER BY parent_code , sort

SELECT 

FROM t_consts_classify
WHERE id = #{id, jdbcType=INTEGER}

mapper.update:
UPDATE t_consts_classify
SET del = 1
WHERE id = #{id, jdbcType=INTEGER}
